const CategorySlider = ({ getCategories }) => {
  const scrollRef = useRef(null);

  const scroll = (direction) => {
    const { current } = scrollRef;
    if (direction === "left") {
      current.scrollBy({ left: -300, behavior: "smooth" });
    } else {
      current.scrollBy({ left: 300, behavior: "smooth" });
    }
  };

  return (
    <div className="w-full px- py-6 relative ">
      <h2 className="text-2xl font-bold mb-4 text-center">Shop by Category</h2>

      {/* Scroll buttons */}
      <button
        onClick={() => scroll("left")}
        className="hidden lg:flex absolute left-0 top-1/2 transform -translate-y-1/2 z-10 bg-white border border-gray-300 rounded-full shadow-md p-2 hover:bg-green-100"
      >
        
      </button>
      <button
        onClick={() => scroll("right")}
        className="hidden lg:flex absolute right-0 top-1/2 transform -translate-y-1/2 z-10 bg-white border border-gray-300 rounded-full shadow-md p-2 hover:bg-green-100"
      >
        
      </button>

      {/* Slider */}
      <div
        ref={scrollRef}
        className="flex gap-4 overflow-x-auto scroll-smooth scrollbar-hide px-10"
      >
        {Categories.map((category, index) => (
          <div
            key={index}
            className="flex flex-col items-center flex-shrink-0 w-24 md:w-28"
          >
            <div
              className="w-20 h-20 md:w-24 md:h-24 rounded-full overflow-hidden border-2 border-green-500 shadow-md hover:scale-105 transition-transform duration-300 ease-in-out"
              onClick={() => getCategories(category.name)}
            >
              <img
                src={category.image}
                alt={category.name}
                className="w-full h-full object-cover"
              />
            </div>
            <p className="text-center text-xs md:text-sm mt-2 font-medium">
              {category.name}
            </p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CategorySlider;
